/*连接查询*/SELECT  *     FROM  Course  FIRST,Course  SECOND     WHERE FIRST.Cpno = SECOND.Cno;SELECT  FIRST.Cno,second.Cno     FROM  Course  FIRST,Course  SECOND     WHERE FIRST.Cpno = SECOND.Cno;SELECT  Student.Sno,Sname,Ssex,Sage,Sdept,Cno,Grade /*自然连接*/ FROM     Student,SC WHERE  Student.Sno = SC.Sno;SELECT  Student.Sno,Sname,Ssex,Sage,Sdept,Cno,Grade /*左外连接*/ FROM     Student left join SC on  Student.Sno = SC.Sno;SELECT Student.Sno,Sname,Ssex,Sage,Sdept,Cno,Grade    FROM  Student  LEFT outer JOIN SC ON (Student.Sno=SC.Sno);SELECT  FIRST.Cno,SECOND.Cpno                       /*自身连接*/     FROM  Course  FIRST,Course  SECOND     WHERE FIRST.Cpno = SECOND.Cno;SELECT Student.Sno, Sname,SC.Grade                  /*复合条件连接*/		FROM    Student, SC		WHERE Student.Sno = SC.Sno AND           SC.Cno= 2 AND SC.Grade > 85;SELECT Student.Sno, Sname,SC.Grade		FROM    Student, SC		WHERE Student.Sno = SC.Sno AND           SC.Cno= '2' AND SC.Grade > 85;SELECT Student.Sno,Sname,Cname,Grade               /*多表连接*/   FROM    Student,SC,Course       WHERE Student.Sno = SC.Sno                    and SC.Cno = Course.Cno;select *   from SC right join Course on (SC.Cno=Course.Cno)    